// src/pages/AddPayment.js
import React, { useEffect, useState } from "react";

function AddPayment() {
  const [debts, setDebts] = useState([]);
  const [debtId, setDebtId] = useState("");
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");

  useEffect(() => {
    const fetchDebts = async () => {
      const res = await fetch("/api/debts");
      const data = await res.json();
      setDebts(data.filter((d) => d.status === "open")); // Nur offene
    };
    fetchDebts();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!debtId || !from || !to || !amount || !date) {
      alert("Bitte alle Felder ausfÃ¼llen.");
      return;
    }

    await fetch("/api/payments", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        debtId,
        from,
        to,
        amount: parseFloat(amount),
        date,
      }),
    });

    alert("Zahlung gespeichert!");
    setDebtId("");
    setFrom("");
    setTo("");
    setAmount("");
    setDate("");
  };

  return (
    <div>
      <h2>ðŸ’¸ Zahlung erfassen</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Schuld auswÃ¤hlen:</label>
          <select
            value={debtId}
            onChange={(e) => {
              const selected = debts.find((d) => d._id === e.target.value);
              setDebtId(e.target.value);
              if (selected) {
                setFrom(selected.debtor);
                setTo(selected.creditor);
              }
            }}
            required
          >
            <option value="">-- auswÃ¤hlen --</option>
            {debts.map((d) => (
              <option key={d._id} value={d._id}>
                {d.debtor} â†’ {d.creditor} | {d.amount.toFixed(2)} CHF |{" "}
                {d.description}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Zahlender:</label>
          <input
            type="text"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            required
          />
        </div>

        <div>
          <label>EmpfÃ¤nger:</label>
          <input
            type="text"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Betrag (CHF):</label>
          <input
            type="number"
            step="0.01"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Datum:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button type="submit">âž• Zahlung speichern</button>
      </form>
    </div>
  );
}

export default AddPayment;
