import React, { useEffect, useState } from "react";

function Overview() {
  const currentUser = "Jascha";
  const [debts, setDebts] = useState([]);

  const fetchDebts = async () => {
    const res = await fetch(`/api/debts?user=${currentUser}`);
    const data = await res.json();
    setDebts(data);
  };

  useEffect(() => {
    fetchDebts();
  }, []);

  const handleMarkAsPaid = async (id) => {
    await fetch(`/api/debts/${id}/pay`, {
      method: "PATCH",
    });
    fetchDebts();
  };

  // Gruppiere Schulden pro Gegenperson
  const grouped = {};
  debts.forEach((debt) => {
    const otherPerson =
      debt.creditor === currentUser ? debt.debtor : debt.creditor;

    if (!grouped[otherPerson]) {
      grouped[otherPerson] = {
        entries: [],
        total: 0,
      };
    }

    const isCreditor = debt.creditor === currentUser;
    const signedAmount = isCreditor ? debt.amount : -debt.amount;

    grouped[otherPerson].entries.push({
      ...debt,
      signedAmount,
      isCreditor,
    });

    grouped[otherPerson].total += signedAmount;
  });

  return (
    <div>
      <h2>Übersicht deiner Schulden</h2>
      {Object.keys(grouped).length === 0 && <p>Keine Schulden gefunden.</p>}

      {Object.entries(grouped).map(([person, { entries, total }]) => (
        <div key={person}>
          <h3>{person}</h3>

          <ul>
            {entries.map((debt) => (
              <li key={debt._id}>
                {debt.isCreditor ? (
                  <span>
                    <strong>{debt.debtor}</strong> schuldet dir{" "}
                    <strong>
                      {debt.amount.toLocaleString("de-CH", {
                        style: "currency",
                        currency: "CHF",
                      })}
                    </strong>{" "}
                    für "{debt.description || "ohne Beschreibung"}" (
                    {new Date(debt.date).toLocaleDateString("de-CH")})
                  </span>
                ) : (
                  <span>
                    Du schuldest <strong>{debt.creditor}</strong>{" "}
                    <strong>
                      {debt.amount.toLocaleString("de-CH", {
                        style: "currency",
                        currency: "CHF",
                      })}
                    </strong>{" "}
                    für "{debt.description || "ohne Beschreibung"}" (
                    {new Date(debt.date).toLocaleDateString("de-CH")})
                  </span>
                )}
                {debt.status === "paid" ? (
                  <span>✔️ bezahlt</span>
                ) : (
                  <button onClick={() => handleMarkAsPaid(debt._id)}>
                    als bezahlt markieren
                  </button>
                )}
              </li>
            ))}
          </ul>

          <p>
            <strong>Saldo mit {person}:</strong>{" "}
            <span
              style={{
                color: total > 0 ? "green" : total < 0 ? "red" : "gray",
              }}
            >
              {total.toLocaleString("de-CH", {
                style: "currency",
                currency: "CHF",
              })}
            </span>
          </p>
        </div>
      ))}
    </div>
  );
}

export default Overview;
