import React, { useEffect, useState } from "react";

function Subscriptions() {
  const currentUser = "Jascha";

  const [subscriptions, setSubscriptions] = useState([]);
  const [availableParticipants, setAvailableParticipants] = useState([]);

  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const [startDate, setStartDate] = useState("");
  const [participants, setParticipants] = useState([]);
  const [singleDebt, setSingleDebt] = useState({
    creditor: "",
    debtor: "",
    amount: "",
    description: "",
    date: "",
  });

  // Daten laden: Abos + Teilnehmer
  useEffect(() => {
    const fetchData = async () => {
      const resSubs = await fetch(`/api/subscriptions?user=${currentUser}`);
      const subs = await resSubs.json();
      setSubscriptions(subs);

      const resParts = await fetch(`/api/participants?user=${currentUser}`);
      const parts = await resParts.json();
      setAvailableParticipants(parts);
    };

    fetchData();
  }, []);

  // Teilnehmer hinzuf√ºgen
  const handleAddParticipant = () => {
    const usedNames = participants.map((p) => p.name);
    const unused = availableParticipants.find(
      (p) => !usedNames.includes(p.name)
    );
    if (!unused) return;
    setParticipants([
      ...participants,
      { name: unused.name, share: 0, isCustom: false },
    ]);
  };

  // Teilnehmer l√∂schen
  const handleRemoveParticipant = (index) => {
    const updated = [...participants];
    updated.splice(index, 1);
    setParticipants(updated);
  };

  // Teilnehmerfelder √§ndern
  const handleParticipantChange = (index, field, value) => {
    const updated = [...participants];
    updated[index][field] = value;
    if (field === "share") updated[index].isCustom = true;
    setParticipants(updated);
  };

  // Anteil zur√ºcksetzen (z.‚ÄØB. per Doppelklick)
  const resetShare = (index) => {
    const updated = [...participants];
    updated[index].isCustom = false;
    updated[index].share = 0;
    setParticipants(updated);
  };

  // Dynamische Anteilverteilung
  useEffect(() => {
    const total = parseFloat(amount);
    if (!total || participants.length === 0) return;

    const fixed = participants.filter((p) => p.isCustom);
    const dynamic = participants.filter((p) => !p.isCustom);

    const fixedSum = fixed.reduce(
      (sum, p) => sum + parseFloat(p.share || 0),
      0
    );
    const remaining = total - fixedSum;
    const perPerson = dynamic.length > 0 ? remaining / dynamic.length : 0;

    const updated = participants.map((p) => {
      if (p.isCustom) return p;
      return { ...p, share: perPerson.toFixed(2) };
    });

    // Nur aktualisieren, wenn sich etwas √§ndert
    const changed =
      participants.length !== updated.length ||
      participants.some(
        (p, i) => p.share !== updated[i].share || p.name !== updated[i].name
      );

    if (changed) {
      setParticipants(updated);
    }
  }, [amount, participants]);

  // Abo absenden
  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      name,
      amount: parseFloat(amount),
      startDate,
      createdBy: currentUser,
      participants: participants.map((p) => ({
        name: p.name,
        share: parseFloat(p.share),
      })),
    };

    await fetch("/api/subscriptions", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    // Reset + Neu laden
    setName("");
    setAmount("");
    setStartDate("");
    setParticipants([]);
    const resSubs = await fetch(`/api/subscriptions?user=${currentUser}`);
    const subs = await resSubs.json();
    setSubscriptions(subs);
  };

  return (
    <div>
      <h2>Abo erstellen</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Abo-Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Gesamtbetrag"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          required
        />

        <h4>Teilnehmeranteile</h4>
        {participants.map((p, index) => (
          <div key={index}>
            <select
              value={p.name}
              onChange={(e) =>
                handleParticipantChange(index, "name", e.target.value)
              }
            >
              {availableParticipants.map((option) => (
                <option
                  key={option._id}
                  value={option.name}
                  disabled={participants.some(
                    (pp, i) => i !== index && pp.name === option.name
                  )}
                >
                  {option.name}
                </option>
              ))}
            </select>

            <input
              type="number"
              value={p.share}
              onChange={(e) =>
                handleParticipantChange(
                  index,
                  "share",
                  parseFloat(e.target.value)
                )
              }
              onDoubleClick={() => resetShare(index)}
            />

            <button
              type="button"
              onClick={() => handleRemoveParticipant(index)}
            >
              üóëÔ∏è
            </button>
          </div>
        ))}

        <button type="button" onClick={handleAddParticipant}>
          ‚ûï Teilnehmer hinzuf√ºgen
        </button>

        <button type="submit">‚úÖ Abo speichern</button>
      </form>

      <h3>Meine Abos</h3>
      <ul>
        {subscriptions.map((sub) => (
          <li key={sub._id}>
            {sub.name} ‚Äì {sub.amount}‚Ç¨ ‚Äì Start:{" "}
            {new Date(sub.startDate).toLocaleDateString("de-CH")}
            <ul>
              {sub.participants.map((p, i) => (
                <li key={i}>
                  {p.name}: {p.share}‚Ç¨
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
      <h2>Einmalige Schuld erfassen</h2>
      <form
        onSubmit={async (e) => {
          e.preventDefault();
          if (
            !singleDebt.creditor ||
            !singleDebt.debtor ||
            !singleDebt.amount ||
            !singleDebt.date
          ) {
            alert("Bitte alle Felder ausf√ºllen.");
            return;
          }

          await fetch("/api/debts", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(singleDebt),
          });

          alert("Schuld gespeichert!");

          setSingleDebt({
            creditor: "",
            debtor: "",
            amount: "",
            description: "",
            date: "",
          });
        }}
      >
        <select
          value={singleDebt.creditor}
          onChange={(e) =>
            setSingleDebt({ ...singleDebt, creditor: e.target.value })
          }
          required
        >
          <option value="">Gl√§ubiger w√§hlen</option>
          {availableParticipants.map((p) => (
            <option key={p._id} value={p.name}>
              {p.name}
            </option>
          ))}
        </select>

        <select
          value={singleDebt.debtor}
          onChange={(e) =>
            setSingleDebt({ ...singleDebt, debtor: e.target.value })
          }
          required
        >
          <option value="">Schuldner w√§hlen</option>
          {availableParticipants.map((p) => (
            <option key={p._id} value={p.name}>
              {p.name}
            </option>
          ))}
        </select>

        <input
          type="number"
          placeholder="Betrag"
          value={singleDebt.amount}
          onChange={(e) =>
            setSingleDebt({ ...singleDebt, amount: parseFloat(e.target.value) })
          }
          required
        />

        <input
          type="text"
          placeholder="Beschreibung"
          value={singleDebt.description}
          onChange={(e) =>
            setSingleDebt({ ...singleDebt, description: e.target.value })
          }
        />

        <input
          type="date"
          value={singleDebt.date}
          onChange={(e) =>
            setSingleDebt({ ...singleDebt, date: e.target.value })
          }
          required
        />

        <button type="submit">‚ûï Einmalige Schuld erfassen</button>
      </form>
    </div>
  );
}

export default Subscriptions;
