// src/pages/PersonDetails.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function PersonDetails() {
  const currentUser = "Jascha";
  const { name } = useParams();
  const [debts, setDebts] = useState([]);
  const [paymentInputs, setPaymentInputs] = useState({}); // fÃ¼r BetrÃ¤ge je Schuld

  useEffect(() => {
    fetchDebts();
  }, [name]);

  const fetchDebts = async () => {
    const res = await fetch(`/api/debts?user=${currentUser}`);
    const data = await res.json();
    // Nur EintrÃ¤ge mit dieser Person
    const filtered = data.filter(
      (d) =>
        (d.creditor === name && d.debtor === currentUser) ||
        (d.debtor === name && d.creditor === currentUser)
    );
    setDebts(filtered);
  };

  const handlePaymentChange = (id, value) => {
    setPaymentInputs((prev) => ({ ...prev, [id]: value }));
  };

  const handleAddPayment = async (debt) => {
    const amount = parseFloat(paymentInputs[debt._id]);
    if (!amount || amount <= 0) {
      alert("Bitte einen gÃ¼ltigen Betrag eingeben");
      return;
    }

    // API-Aufruf Zahlung erfassen
    await fetch("/api/payments", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        debtId: debt._id,
        amount,
        date: new Date(), // jetzt
        paidBy: currentUser,
      }),
    });

    setPaymentInputs((prev) => ({ ...prev, [debt._id]: "" }));
    fetchDebts();
  };

  const getLabel = (debt) => {
    if (debt.creditor === currentUser) {
      return `Du bekommst`;
    } else {
      return `Du schuldest`;
    }
  };

  // Saldo berechnen
  const total = debts.reduce((sum, d) => {
    const val = d.amount - (d.paidAmount || 0);
    if (d.status === "paid") return sum; // Ignorieren
    if (d.creditor === currentUser) return sum + val; // Du bekommst
    else return sum - val; // Du schuldest
  }, 0);

  return (
    <div>
      <h2>Details zu {name}</h2>
      <p>
        <strong>Saldo:</strong> {total.toFixed(2)} CHF
      </p>
      {debts.length === 0 && <p>Keine EintrÃ¤ge gefunden.</p>}
      <ul>
        {debts.map((debt) => {
          const rest = (debt.amount - (debt.paidAmount || 0)).toFixed(2);
          return (
            <li key={debt._id}>
              {getLabel(debt)} {debt.amount.toFixed(2)} CHF fÃ¼r{" "}
              {debt.description} am{" "}
              {new Date(debt.date).toLocaleDateString("de-CH")}
              <br />
              {debt.status !== "paid" && (
                <>
                  Noch offen: {rest} CHF
                  <div>
                    <input
                      type="number"
                      step="0.01"
                      placeholder="Betrag"
                      value={paymentInputs[debt._id] || ""}
                      onChange={(e) =>
                        handlePaymentChange(debt._id, e.target.value)
                      }
                    />
                    <button onClick={() => handleAddPayment(debt)}>
                      ðŸ’¸ Zahlung erfassen
                    </button>
                  </div>
                </>
              )}
              {debt.status === "paid" && <span> â€“ bezahlt</span>}
              {debt.payments && debt.payments.length > 0 && (
                <div>
                  <strong>Historie:</strong>
                  <ul>
                    {debt.payments.map((p, i) => (
                      <li key={i}>
                        {new Date(p.date).toLocaleDateString("de-CH")} â€“{" "}
                        {p.amount.toFixed(2)} CHF
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default PersonDetails;
