// frontend/src/pages/Overview.js
import React, { useEffect, useState } from "react";

function Debts() {
  const currentUser = "Jascha";
  const [debts, setDebts] = useState([]);

  const fetchDebts = async () => {
    const res = await fetch(`/api/debts?user=${currentUser}`);
    const data = await res.json();
    setDebts(data);
  };

  useEffect(() => {
    fetchDebts();
  }, []);

  const handleMarkAsPaid = async (id) => {
    await fetch(`/api/debts/${id}/pay`, {
      method: "PATCH",
    });
    fetchDebts();
  };

  return (
    <div>
      <h2>Offene Schulden</h2>
      {debts.length === 0 && <p>Keine Schulden gefunden.</p>}
      <ul>
        {debts.map((debt) => (
          <li key={debt._id}>
            <strong>{debt.debtor}</strong> schuldet dir{" "}
            <strong>{debt.amount.toFixed(2)}€</strong> für{" "}
            {debt.description || "Unbekannt"} (
            {new Date(debt.date).toLocaleDateString("de-CH")}) –{" "}
            {debt.status === "paid" ? (
              <span style={{ color: "green" }}>bezahlt</span>
            ) : (
              <button onClick={() => handleMarkAsPaid(debt._id)}>
                als bezahlt markieren
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Debts;
