// src/pages/Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const currentUser = "Jascha";
  const [, setDebts] = useState([])
  const [summary, setSummary] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDebts = async () => {
      const res = await fetch(`/api/debts?user=${currentUser}`);
      const data = await res.json();
      setDebts(data);

      const grouped = {};

      data.forEach((debt) => {
        if (debt.status !== "open") return;

        const other =
          debt.creditor === currentUser ? debt.debtor : debt.creditor;
        const amount =
          debt.creditor === currentUser ? debt.amount : -debt.amount;

        if (!grouped[other]) grouped[other] = 0;
        grouped[other] += amount;
      });

      const summaryArray = Object.entries(grouped).map(([name, total]) => ({
        name,
        total,
      }));

      setSummary(summaryArray);
    };

    fetchDebts();
  }, []);

  return (
    <div>
      <h2>Ãœberblick deiner Schulden</h2>
      {summary.length === 0 && <p>Keine offenen Schulden.</p>}
      <ul>
        {summary.map((entry) => (
          <li key={entry.name}>
            {entry.total > 0 &&
              `${entry.name} schuldet dir ${entry.total.toFixed(2)} CHF`}
            {entry.total < 0 &&
              `Du schuldest ${entry.name} ${Math.abs(entry.total).toFixed(
                2
              )} CHF`}
            {entry.total === 0 && `${entry.name}: ausgeglichen`}
            <button onClick={() => navigate(`/dashboard/${entry.name}`)}>
              Details anzeigen
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
