import React, { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import "../styles/PersonDetails.css";

function PersonDetails() {
  const currentUser = "Jascha";
  const { name } = useParams();
  const [debts, setDebts] = useState([]);
  const [paymentInputs, setPaymentInputs] = useState({});

  const fetchDebts = useCallback(async () => {
    const res = await fetch(`/api/debts?user=${currentUser}`);
    const data = await res.json();
    const filtered = data.filter(
      (d) =>
        (d.creditor === name && d.debtor === currentUser) ||
        (d.debtor === name && d.creditor === currentUser)
    );
    setDebts(filtered);
  }, [currentUser, name]);

  useEffect(() => {
    fetchDebts();
  }, [fetchDebts]);

  const handlePaymentChange = (id, value) => {
    setPaymentInputs((prev) => ({ ...prev, [id]: value }));
  };

  const handleAddPayment = async (debt) => {
    const amount = parseFloat(paymentInputs[debt._id]);
    if (!amount || amount <= 0) {
      alert("Bitte einen gÃ¼ltigen Betrag eingeben");
      return;
    }

    await fetch("/api/payments", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        debtId: debt._id,
        amount,
        date: new Date(),
        paidBy: currentUser,
      }),
    });

    setPaymentInputs((prev) => ({ ...prev, [debt._id]: "" }));
    fetchDebts();
  };

  const getLabel = (debt) => {
    return debt.creditor === currentUser ? `Du bekommst` : `Du schuldest`;
  };

  const total = debts.reduce((sum, d) => {
    const val = d.amount - (d.paidAmount || 0);
    if (d.status === "paid") return sum;
    if (d.creditor === currentUser) return sum + val;
    else return sum - val;
  }, 0);

  return (
    <div className="detail-view">
      <div className="detail-header">
        <h2>ð Detailansicht â {name}</h2>
        <a href="/">ZurÃ¼ck</a>
      </div>

      <div className="detail-section">
        <h3>ð Saldo</h3>
        <p>
          <strong>{total.toFixed(2)} CHF</strong>
        </p>
      </div>

      {debts.length === 0 ? (
        <p>Keine EintrÃ¤ge gefunden.</p>
      ) : (
        <>
          <div className="detail-section">
            <h3>ð Schuldenverlauf</h3>
            {debts.map((debt) => {
              const rest = (debt.amount - (debt.paidAmount || 0)).toFixed(2);
              return (
                <div key={debt._id} className="detail-row">
                  <div>
                    <span className="label">{getLabel(debt)}</span>{" "}
                    <strong>{debt.amount.toFixed(2)} CHF</strong>
                    <br />
                    <span>{debt.description}</span> â{" "}
                    {new Date(debt.date).toLocaleDateString("de-CH")}
                  </div>
                  {debt.status !== "paid" && (
                    <div className="payment-form">
                      <span>Noch offen: {rest} CHF</span>
                      <input
                        type="number"
                        step="0.01"
                        placeholder="Betrag"
                        value={paymentInputs[debt._id] || ""}
                        onChange={(e) =>
                          handlePaymentChange(debt._id, e.target.value)
                        }
                      />
                      <button onClick={() => handleAddPayment(debt)}>
                        Zahlung erfassen
                      </button>
                    </div>
                  )}
                  {debt.status === "paid" && (
                    <span className="status-badge status-bezahlt">bezahlt</span>
                  )}

                  {debt.payments && debt.payments.length > 0 && (
                    <div className="payment-history">
                      <strong>Historie:</strong>
                      <ul>
                        {debt.payments.map((p, i) => (
                          <li key={i}>
                            {new Date(p.date).toLocaleDateString("de-CH")} â{" "}
                            {p.amount.toFixed(2)} CHF
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </>
      )}
    </div>
  );
}

export default PersonDetails;
